let currentURL = window.location.href;

if (currentURL.indexOf("/morari/forgotpassword") == -1 && currentURL.indexOf("/morari/login") == -1 && currentURL.indexOf("/morari/register") == -1) {
    window.onload = function () {
        document.body.style.display = 'block';

        // 查看權限
        fetch('/morari/admin/camper/api/userroles')
            .then(response => response.json())
            .then(data => {
                // 判斷是否有 SUPERADMIN 或 ADMIN 角色
                if (data.some(role => role.authority === 'SUPERADMIN' || role.authority === 'ADMIN')) {
                    // 顯示編輯按鈕
                    document.getElementById('dashboard').style = '';
                    document.getElementById('forumadmin').style = '';
                    document.getElementById('teamadmin').style = '';
                    document.getElementById('alluser').style = '';
                    document.getElementById('mallitem').style = '';
                    document.getElementById('mallall').style = '';
                    document.getElementById('mallproduct').style = '';
                    document.getElementById('mallorder').style = '';
                    document.getElementById('campall').style = '';
                    document.getElementById('camporderall').style = '';
                    document.getElementById('usercamporder').style = '';
                    document.getElementById('workitem').style = '';
                    document.getElementById('workall').style = '';
                    document.getElementById('resumeall').style = '';
                    document.getElementById('company').style = '';
                }
                if (data.some(role => role.authority === 'MALL')) {
                    // 顯示編輯按鈕
                    document.getElementById('mallitem').style = '';
                    document.getElementById('mallproduct').style = '';
                    document.getElementById('mallorder').style = '';
                }
                if (data.some(role => role.authority === 'COMPANY')) {
                    // 顯示編輯按鈕
                    document.getElementById('workitem').style = '';
                    document.getElementById('company').style = '';
                }
            });

        fetch("/morari/guest/camper/api/shot")
            .then(response => response.text())
            .then(shotUrl => {
                document.querySelector('.userShot').src = shotUrl;
            })
    }
};